  PRIMARY KEY (`news_id`) USING BTREE,
  INDEX `FK_t_news`(`section_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 101 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Compact;

